import '../styles/WhiteKey.css'

function WhiteKey(props) {

  let path = require("./" + `../audio/softest/${props.note}.mp3`).default;

  const noteAudio = new Audio(`../audio/softest/${props.note}.mp3`)
  const playNote = () => {
    noteAudio.currentTime = 0
    noteAudio.play()
  }

  const handleClick = () => {
    playNote()
  }

  return (
    <div className="white-key" note = {props.note} onClick = {handleClick}></div>
  )
}

export default WhiteKey
