import '../styles/BlackKey.css'

function BlackKey(props) {

  const path = (`audio/softest/${props.note}.mp3`).replace('#', '%23')
  const audio = new Audio(path)

  const playNote = () => {
    audio.play()
  }

  const handleMouseDown = () => {
    playNote()
  }

  const handleMouseUp = () => {
    audio.pause()
    audio.currentTime = 0
  }

  return (
    <div className='black-key' note = {props.note} onMouseDown = {handleMouseDown} onMouseUp = {handleMouseUp}></div>
  )
}

export default BlackKey
