import '../styles/WhiteKey.css'

function WhiteKey(props) {

  const path = `audio/softest/${props.note}.mp3`
  const audio = new Audio(path)

  const playNote = () => {
    audio.play()
  }

  const handleMouseDown = () => {
    playNote()
  }

  const handleMouseUp = () => {
    audio.animate([ { transform: 'scale(1.2)' }, { transform: 'scale(1)' } ], 100)
    audio.currentTime = 0;

  }

  return (
    <div className="white-key" note = {props.note} onMouseDown = {handleMouseDown} onMouseUp = {handleMouseUp}></div>
  )
}

export default WhiteKey
