import '../styles/WhiteKey.css'

function WhiteKey(props) {

  const path = `audio/softest/${props.note}.mp3`
  const audio = new Audio(path)

  const playNote = () => {
    audio.play()
  }

function fade(){
  audio.volume = 0.5;
  setTimeout(function(){audio.volume = 0.4;}, 100);
  setTimeout(function(){audio.volume = 0.3;}, 200);
  setTimeout(function(){audio.volume = 0.2;}, 300);
  setTimeout(function(){audio.volume = 0.1;}, 400);
  setTimeout(function(){audio.volume = 0;}, 500);
}

  const handleMouseDown = () => {
    playNote()
  }

  const handleMouseUp = () => {
    fade()
    audio.volume = 1;
    audio.currentTime = 0;

  }

  return (
    <div className="white-key" note = {props.note} onMouseDown = {handleMouseDown} onMouseUp = {handleMouseUp}></div>
  )
}

export default WhiteKey
